import { CustomError } from '@twurple/common';
/**
 * Thrown whenever a HTTP error occurs. Some HTTP errors are handled in the library when they're expected.
 */
export class HttpStatusCodeError extends CustomError {
    /** @private */
    constructor(_statusCode, statusText, _body, isJson) {
        super(`Encountered HTTP status code ${_statusCode}: ${statusText}\n\nBody:\n${!isJson && _body.length > 150 ? `${_body.substr(0, 147)}...` : _body}`);
        this._statusCode = _statusCode;
        this._body = _body;
    }
    get statusCode() {
        return this._statusCode;
    }
    get body() {
        return this._body;
    }
}
